function setCookie(e,o,t){var n=new Date;n.setDate(n.getDate()+t),n.setHours(n.getHours()-8),document.cookie=encodeURIComponent(e)+"="+encodeURIComponent(o)+";path=/;expires="+n}function getCookie(e){var o=document.cookie;if(o){for(var t=o.split("; "),n=0;n<t.length;n++){var r=t[n].split("="),i=decodeURIComponent(r[0]),c=decodeURIComponent(r[1]);if(i==e)return c}return""}return""}